var a =  [
    {
        name:"chandu",
        id:123,
        "maths":23,
        "science":56,
        "social":32
    },
    {
        name:"manju",
        id:125,
        "maths":74,
        "science":24,
        "social":46
    },
    {
        name:"raju",
        id:127,
        "maths":89,
        "science":23,
        "social":32
    }
]

fun resulte(marks)= if (marks >35) "pass" else "fail"
output application/json
---
a map {
    name:$.name,
    id:$.id,
    subjectwisedetails: {
        maths: {
            subject:$.maths,
            result: resulte($.maths)
        },
        science: {
            subject:$.science,
            result: resulte($.science)
        },
        social: {
            subject:$.social,
            result: resulte($.social)
        }
    }

}

